	private void parseXPiolet(String sss) {
		int xp[] = new int[36], yp[] = new int[36];

		String na = "";
		if (sss.toUpperCase().indexOf("(NM:") >= 0)
			na = sss.substring(sss.toUpperCase().indexOf("(NM:") + 4);
		if (na.indexOf(")") >= 0)
			na = na.substring(0, na.indexOf(")"));
		shapeName = na;

		String au = "";
		if (sss.toUpperCase().indexOf("(AU:") >= 0)
			au = sss.substring(sss.toUpperCase().indexOf("(AU:") + 4);
		if (au.indexOf(")") >= 0)
			au = au.substring(0, au.indexOf(")"));
		shapeAuthor = au;

		mainGun = parsePoint("(MG:", sss);
		leftGun = parsePoint("(LG:", sss);
		rightGun = parsePoint("(RG:", sss);
		engine = parsePoint("(EN:", sss);
		leftLight = parsePoint("(LL:", sss);
		rightLight = parsePoint("(RL:", sss);

		// missileRack=parsePoint("(MR:",sss);

		// "xpilot.shipShape: (NM:default)(AU:Unknown)(SH: 15,0 -9,8 -9,-8)(MG:
		// 15,0)(MR: 15,0)(LL: -9,8)(RL: -9,-8)(EN: -9,0)",
		String shapeString = sss.substring(sss.indexOf("(SH:") + 4).trim();
		shapeString = shapeString.substring(0, shapeString.indexOf(")"));
		int count = 0;
		while (shapeString.indexOf(",") >= 0) {
			String x = shapeString.substring(0, shapeString.indexOf(","))
					.trim();
			shapeString = shapeString.substring(shapeString.indexOf(",") + 1)
					.trim();
			String y = shapeString;
			if (y.indexOf(" ") >= 0) {
				y = shapeString.substring(0, shapeString.indexOf(" ")).trim();
				shapeString = shapeString.substring(
						shapeString.indexOf(" ") + 1).trim();
			}

			xp[count] = Integer.parseInt(x);
			yp[count] = Integer.parseInt(y);
			xp[count + 1] = xp[0];
			yp[count + 1] = yp[0];
			count++;
		}
		shape = new Polygon(xp, yp, count);
	}

	private Point parsePoint(String string, String sss) {
		String res = "";
		int x = 0, y = 0;

		if (sss.indexOf(string) >= 0)
			res = sss.substring(sss.indexOf(string) + string.length());
		if (res.indexOf(")") >= 0)
			res = res.substring(0, res.indexOf(")")).trim();
		if (res.indexOf(",") >= 0) {
			x = Integer.parseInt(res.substring(0, res.indexOf(",")).trim());
			res = res.substring(res.indexOf(",") + 1).trim();
			if (res.indexOf(" ") >= 0)
				res = res.substring(0, res.indexOf(" ")).trim();
			y = Integer.parseInt(res);
		}
		return new Point(x, y);
	}

