	public ShipShape(int i) {
		index = i;
		if (parsedShape == null) {
			parsedShape = new Polygon[shipDefineStrings.length];
			parsedMainGun = new Point[shipDefineStrings.length];
			parsedLeftGun = new Point[shipDefineStrings.length];
			parsedRightGun = new Point[shipDefineStrings.length];
			parsedLeftLight = new Point[shipDefineStrings.length];
			parsedRightLight = new Point[shipDefineStrings.length];
			parsedEngin = new Point[shipDefineStrings.length];
			parsedMissleRack = new Point[shipDefineStrings.length];
			parsedShapeAuthor = new String[shipDefineStrings.length];
			parsedShapeName = new String[shipDefineStrings.length];
		}
		if (parsedShape[index] == null) {
			parseXPiolet(shipDefineStrings[index]);
			parsedShape[index] = shape;
			parsedShapeAuthor[index] = shapeAuthor;
			parsedShapeName[index] = shapeName;
		} else {
			shape = parsedShape[index];
			shapeAuthor = parsedShapeAuthor[index];
			shapeName = parsedShapeName[index];
		}
	}

