	public static void main(String s[]) {
		final JFrame j = new JFrame();
		j.setSize(400, 600);
		j.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
		j.setVisible(true);
		j.addMouseListener(new MouseListener() {

			public void mouseClicked(MouseEvent e) {
				count++;
				if (count == 0 || count > 5) {
					count = 0;
					show = shipTypeAlien;
				}
				if (count == 1)
					show = shipTypePlane;
				if (count == 2)
					show = shipTypeSilly;
				if (count == 3)
					show = shipTypeSpace;
				if (count == 4)
					show = shipTypeTooSmall;
				if (count == 5)
					show = shipTypeUnknown;

			}

			public void mousePressed(MouseEvent e) {
			}

			public void mouseReleased(MouseEvent e) {
			}

			public void mouseEntered(MouseEvent e) {
			}

			public void mouseExited(MouseEvent e) {
			}
		});
		new Thread() {

			public void run() {
				int rot = 0;
				int maxrot = 120;
				while (true) {
					if (i == null || i.getWidth(null) != j.getWidth()
							|| i.getHeight(null) != j.getHeight()) {
						i = j.createImage(j.getWidth(), j.getHeight());
					}
					Graphics g = i.getGraphics();
					g.setColor(Color.black);
					g.fillRect(0, 0, j.getWidth(), j.getHeight());
					g.translate(30, 50);
					g.setColor(Color.white);
					int sqr = (int) (Math.sqrt(show.length));
					int dw = (j.getWidth() - 80) / sqr;
					int dh = (j.getHeight() - 40) / sqr;
					for (int a = 0; a < sqr + 1; a++) {
						for (int b = 0; b < sqr; b++) {
							g.translate(dw * a, dh * b + (a % 2 == 0 ? 20 : 0));
							int count = sqr * a + b;
							if (count >= show.length)
								continue;
							ShipShape ss = new ShipShape(show[count]);
							ss.rotate(Math.PI * 2 * (1.0 * rot / maxrot));
							g.drawPolygon(ss.shape);
							g.drawString(show[count] + ss.shapeName, 00, 20);
							g.translate(-dw * a, -dh * b
									- (a % 2 == 0 ? 20 : 0));

						}
					}

					try {
						Thread.sleep(30);
					} catch (InterruptedException e) {
						e.printStackTrace();
					}
					rot++;
					if (rot > maxrot) {
						rot = 0;
					}
					Graphics G = j.getGraphics();
					G.drawImage(i, 0, 0, null);
				}
			}

		}.start();
	}

	public void rotate(double d) {
		shape = parsedShape[index];
		int xpoints[] = new int[shape.npoints];
		int ypoints[] = new int[shape.npoints];
		for (int a = 0; a < shape.npoints; a++) {
			xpoints[a] = (int) (shape.xpoints[a] * Math.cos(d) - shape.ypoints[a]
					* Math.sin(d));
			ypoints[a] = (int) (shape.xpoints[a] * Math.sin(d) + shape.ypoints[a]
					* Math.cos(d));
		}
		shape = new Polygon(xpoints, ypoints, shape.npoints);
	}

